  def mapMove(m: Char): Int = m match {
    case '(' => 1
    case ')' => -1
    case _ => 0
  }

  def reduceMove(moves: List[(Char, Int)]): Int = moves match {
    case move :: t => if (move._1 == '(') move._2 - reduceMove(t) else - move._2 + reduceMove(t)
    case Nil => 0
  }
